$(window).load(function(){$(".flexslider").flexslider({animation:"fade",animationLoop:"true",customDirectionNav:$(".custom-navigation .arrow"),start:function(n){$("body").removeClass("loading")}})}),$(".submenu-toggle").on("click",function(n){var e=$(n.currentTarget).parent();e.toggleClass("open")}),$(".nav-list-item.submenu").mouseover(function(n){$(window).width()>618&&($(".nav-list-item").removeClass("open"),$(n.currentTarget).addClass("open"))}),$(".nav-submenu").mouseout(function(n){var e=$(n.currentTarget).parent();$(window).width()>618&&$(e).removeClass("open")});